import React,{Component} from 'react';
import {Table} from 'react-bootstrap';

import {Button,ButtonToolbar} from 'react-bootstrap';
import { AddAssignmentModal } from './AddAssignmentModal';
import { EditAssignmentModal } from './EditAssignmentModal';

export class Home extends Component{

    constructor(props){
        super(props);
        this.state={assignments:[], addModalShow:false, editModalShow: false}
        this.handleSubmit=this.handleSubmit.bind(this);
    }

    refreshList(){
        fetch(process.env.REACT_APP_API+'assignment')
        .then(response=>response.json())
        .then(data=>{
            this.setState({assignments:data});
        })
    }

    componentDidMount(){
        this.refreshList();
    }

    componentDidUpdate(){
        this.refreshList();
    }

    render(){
        const {assignments, assignmentid, assignmentname}=this.state;
        let addModalClose=()=>this.setState({addModalShow:false})
        let editModalClose=()=>this.setState({editModalShow:false})
        return(
            <div className="home">
                <Table border hover>
                    <thead>
                        <tr>
                            <th>Course Subject</th>
                            <th>Course Name</th>
                            <th>Assignment Id</th>
                            <th>Assignment Name</th>
                            <th>Grade (%)</th>
                            <th>Submission Date</th>
                            <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {assignments.map(assignment=>
                            <tr key={assignment.AssignmentId}>
                                <td>{assignment.AssignmentName}</td>
                                <td>{assignment.AssignmentName}</td>
                                <td>{assignment.AssignmentId}</td>
                                <td>{assignment.AssignmentName}</td>
                                <td>{assignment.AssignmentGrade}</td>
                                <td>{assignment.SubmissionDate}</td>
                                <td>
                                <ButtonToolbar>
                                    <Button variant='primary' onClick={()=>this.setState({editModalShow:true})}>
                                        Edit Assignment
                                    </Button>
                                    <AddAssignmentModal show={this.state.editModalShow} onHide={editModalCLose}></AddAssignmentModal>
                                </ButtonToolbar>
                
                                </td>
                            </tr>
                            )}
                    </tbody>
                </Table>

                <ButtonToolbar>
                    <Button variant='primary' onClick={()=>this.setState({addModalShow:true})}>
                        Add Assignment
                    </Button>

                    <AddAssignmentModal show={this.state.addModalShow} onHide={addModalClose}></AddAssignmentModal>
                </ButtonToolbar>
                
            </div>
        )
    }
}
